import React, { useState } from 'react';
// import { useHistory } from 'react-router-dom';
import './Signup.css';


export const Signup = () => {

    // let history = useHistory();
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        let data = { name, email, password };
        
        fetch('/signup', { 
            method: "POST", 
            headers: { 'Accept': 'application/json', 'content-type': 'application/json' }, 
            body: JSON.stringify(data) 
        })
        .then(result => result.json())
        .then((res) => {

            // console.log(res);

            if(res.success){
                alert(res.message);
                // history.push('/verifyaccount');
            }
            else{
                alert(res.message);
                console.log(res.message);
            }
        });
        
    }
    return (
        <div className="sign-up">
        <div className="signup-container">
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" value={name} placeholder="name" onChange={(e) => { setName(e.target.value) }} required/>
                <input type="email" name="email" value={email} placeholder="email" onChange={(e) => { setEmail(e.target.value) }} required/>
                <input type="password" name="password" value={password} placeholder="password" onChange={(e) => { setPassword(e.target.value) }} required/>
                <input type="submit" value="SIGN UP" />
            </form>
        </div>
        </div>
    )
}
