import React,{useState} from 'react';
import { Register } from './Pages/RegisterPage/Register';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import { Signin } from './Pages/SigninPage/Signin';
import { Home } from './Components/Home/Home';
import { ForgotPass } from './Pages/ForgotPass/ForgotPass';
import { Dashboard } from './Components/Dashboard/Dashboard';
import './App.css';

const SecuredRoute = (props)=>{
  console.log(props.component);
  return
    <Route path = {props.path} render={data=>loggedIn ? (
      <props.component {...data}></props.component>):
      <Redirect to={{pathname:"/signin"}}></Redirect>
    }/>

}

function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  return (
    <div>
      <Router>
        <Switch>
          <Route exact path="/" component={Home} />
          <SecuredRoute exact path="/dashboard" ><Dashboard logged={setLoggedIn} /></SecuredRoute>
          <Route exact path="/register" component={Register} />
          <Route exact path="/signin" ><Signin logged={setLoggedIn}/></Route>
          <Route exact path="/forgotpass" component={ForgotPass} />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
