import React,{useState} from 'react';
import {useHistory} from 'react-router-dom';
import './Signup.css';


export const Signup = () => {

    let history = useHistory();
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit=(e)=>{
        e.preventdefault();
        const data = {name,email,password};
        fetch('/signup',{method:"POST",headers:{'Accept':'application/json','content-type':'application/json'},body:JSON.stringify(data)}).then((response)=>{
            response.json().then((result)=>{
                console.log(result);
                history.push('/verifyaccount');
            });
        }).catch((err)=>{
            console.log(err);
        })
    }
    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" value={name} placeholder="name" onChange={(e)=>{setName(e.target.value)}} />
                <input type="email" name="email" value={email} placeholder="email" onChange={(e)=>{setEmail(e.target.value)}} />
                <input type="password" name="password" value={password} placeholder="password" onChange={(e)=>{setPassword(e.target.value)}} />
                <input type="submit" value="SUBMIT" />
            </form>
        </div>
    )
}
